import { useState } from 'react';

export default function WordleSolver() {
  const [guesses, setGuesses] = useState([]);
  const [currentGuess, setCurrentGuess] = useState('');
  const [currentResult, setCurrentResult] = useState('');
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async () => {
    if (currentGuess.length !== 5 || currentResult.length !== 5) {
      alert('Both guess and result must be 5 characters.');
      return;
    }

    const updatedGuesses = [...guesses, { word: currentGuess, result: currentResult }];
    setGuesses(updatedGuesses);
    setLoading(true);

    try {
      const res = await fetch('http://localhost:5000/solve', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ guesses: updatedGuesses }),
      });

      const data = await res.json();
      setResults(data.remaining);
    } catch (err) {
      alert('Error connecting to server.');
      console.error(err);
    } finally {
      setLoading(false);
      setCurrentGuess('');
      setCurrentResult('');
    }
  };

  return (
    <div className="p-6 max-w-xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Wordle Solver</h1>

      <div className="flex gap-2 mb-4">
        <input
          type="text"
          placeholder="Guess (e.g. crane)"
          value={currentGuess}
          maxLength={5}
          onChange={(e) => setCurrentGuess(e.target.value.toLowerCase())}
          className="border px-2 py-1 w-1/2"
        />
        <input
          type="text"
          placeholder="Result (e.g. bybyy)"
          value={currentResult}
          maxLength={5}
          onChange={(e) => setCurrentResult(e.target.value.toLowerCase())}
          className="border px-2 py-1 w-1/2"
        />
        <button onClick={handleSubmit} className="bg-blue-500 text-white px-4 py-1 rounded">
          Submit
        </button>
      </div>

      {loading && <p className="text-gray-500">Solving...</p>}

      {results.length > 0 && (
        <div>
          <h2 className="font-semibold mb-2">Possible Words ({results.length}):</h2>
          <ul className="list-disc list-inside space-y-1">
            {results.map((word, index) => (
              <li key={index}>{word}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
