import { useState } from 'react';

export default function WordleSolver() {
  const [guesses, setGuesses] = useState([]);
  const [currentGuess, setCurrentGuess] = useState('');
  const [currentResult, setCurrentResult] = useState('');
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [finish, setResult] = useState('');

  const resetGame = () => {
    setGuesses([]);
    setCurrentGuess('');
    setCurrentResult('');
    setResults([]);
    setError('');
  };

  const handleSubmit = async () => {
    if (currentGuess.length !== 5 || currentResult.length !== 5) {
      setError('Guess and result must each be 5 characters.');
      return;
    }else if(currentResult === 'ggggg') {
     alert('Congrats you won!');
     resetGame();
     return; 
    }

    setError('');
    const updatedGuesses = [...guesses, { word: currentGuess, result: currentResult }];
    setGuesses(updatedGuesses);
    setLoading(true);

    try {
      const res = await fetch('http://localhost:5000/solve', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ guesses: updatedGuesses }),
      });

      const data = await res.json();
      setResults(data.remaining);
    } catch (err) {
      setError('Error connecting to server. Please make sure Flask is running.');
      console.error(err);
    } finally {
      setLoading(false);
      setCurrentGuess('');
      setCurrentResult('');
    }
  };

  return (
    <div className="p-6 max-w-2xl mx-auto font-sans">
      <h1 className="text-3xl font-bold mb-6 text-center">üîç Wordle Solver</h1>

      <div className="bg-white shadow-md rounded-lg p-4 mb-6">
        <h2 className="text-xl font-semibold mb-2">Enter Your Guess</h2>
        <div className="flex flex-col sm:flex-row gap-2">
          <input
            type="text"
            placeholder="Guess (e.g. crane)"
            value={currentGuess}
            maxLength={5}
            onChange={(e) => setCurrentGuess(e.target.value.toLowerCase())}
            className="border rounded px-3 py-2 w-full sm:w-1/2"
          />
          <input
            type="text"
            placeholder="Result (e.g. bybyy)"
            value={currentResult}
            maxLength={5}
            onChange={(e) => setCurrentResult(e.target.value.toLowerCase())}
            className="border rounded px-3 py-2 w-full sm:w-1/2"
          />
          <button
            onClick={handleSubmit}
            className="bg-green-600 hover:bg-green-700 text-white font-medium px-4 py-2 rounded"
          >
            Submit
          </button>
        </div>
        {error && <p className="text-red-600 mt-2 font-medium">{error}</p>}
      </div>

      {guesses.length > 0 && (
        <div className="mb-6">
          <h3 className="text-lg font-semibold mb-1">üìù Guess History:</h3>
          <ul className="list-disc list-inside text-gray-800">
            {guesses.map((g, idx) => (
              <li key={idx}>
                <span className="font-mono">{g.word}</span> ‚Üí <span className="font-mono">{g.result}</span>
              </li>
            ))}
          </ul>
        </div>
      )}

      {loading && <p className="text-gray-500">Crunching the words...</p>}

      {results.length > 0 && (
        <div className="bg-gray-50 rounded-lg p-4 shadow-inner">
          <h2 className="text-xl font-semibold mb-3">üîé Possible Words ({results.length}):</h2>
          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-2 text-center">
            {results.map((word, index) => (
              <div
                key={index}
                className="bg-white px-3 py-2 rounded border text-gray-800 hover:bg-yellow-100"
              >
                {word}
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}
