import React, { useState } from "react";
import "./index.css"; // make sure this line is here

function App() {
  const [guesses, setGuesses] = useState([]);
  const [currentWord, setCurrentWord] = useState("");
  const [currentResult, setCurrentResult] = useState("");
  const [remainingWords, setRemainingWords] = useState([]);
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (currentWord.length !== 5 || currentResult.length !== 5) {
      setError("Both fields must be exactly 5 characters.");
      return;
    }

    const newGuesses = [...guesses, { word: currentWord, result: currentResult }];
    setGuesses(newGuesses);

    if (currentResult === "ggggg") {
      alert("ðŸŽ‰ You solved it! Resetting...");
      resetGame();
      return;
    }

    try {
      const res = await fetch("http://localhost:5000/solve", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ guesses: newGuesses }),
      });

      const data = await res.json();
      setRemainingWords(data.remaining);
      setError("");
    } catch (err) {
      setError("âŒ Couldn't connect to server.");
    }

    setCurrentWord("");
    setCurrentResult("");
  };

  const resetGame = () => {
    setGuesses([]);
    setCurrentWord("");
    setCurrentResult("");
    setRemainingWords([]);
    setError("");
  };

  return (
    <div>
      <h1>ðŸ§  Wordle Solver</h1>

      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Guess (e.g. crane)"
          value={currentWord}
          onChange={(e) => setCurrentWord(e.target.value.toLowerCase())}
        />
        <input
          type="text"
          placeholder="Result (e.g. bygyg)"
          value={currentResult}
          onChange={(e) => setCurrentResult(e.target.value.toLowerCase())}
        />
        <button type="submit">Submit</button>
      </form>

      {error && <p style={{ color: "red", textAlign: "center" }}>{error}</p>}

      {guesses.length > 0 && (
        <>
          <h2>Guess History</h2>
          <ul>
            {guesses.map((g, i) => (
              <li key={i}>
                <code>{g.word}</code> â€” <code>{g.result}</code>
              </li>
            ))}
          </ul>
        </>
      )}

      {remainingWords.length > 0 && (
        <>
          <h2>Remaining Possible Words</h2>
          <div className="word-grid">
            {remainingWords.map((word, i) => (
              <div className="word-tile" key={i}>
                {word}
              </div>
            ))}
          </div>
        </>
      )}
    </div>
  );
}

export default App;
